import React, { useState } from "react";
import CommonPopup from "./CommonPopup";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
  const [isPopupOpen, setPopupOpen] = useState(false);
  const [formData, setFormData] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Category Added:", formData);
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Category Master</h2>
      <button className="add-btn" onClick={() => setPopupOpen(true)}>+ Add Category</button>

      <CommonPopup isOpen={isPopupOpen} onClose={() => setPopupOpen(false)}>
        <form onSubmit={handleSubmit}>
          <label>Primary Category</label>
          <input type="text" name="primaryCategory" value={formData.primaryCategory || ""} onChange={handleChange} required />

          <label>Sub Category</label>
          <input type="text" name="subCategory" value={formData.subCategory || ""} onChange={handleChange} required />

          <button type="submit">Save</button>
        </form>
      </CommonPopup>
    </div>
  );
};

export default CategoryMaster;
