import React, { useState, useEffect } from "react";
import inventoryService from "../services/inventoryService";
import "../styles/MasterForms.css";

const InventoryMaster = () => {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    const data = await inventoryService.getInventory();
    setInventory(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (item) => {
    setFormData(item);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await inventoryService.deleteInventory(id);
    fetchInventory();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "photo") {
      setFormData({ ...formData, photo: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }
    if (formData._id) {
      await inventoryService.updateInventory(formData._id, data);
    } else {
      await inventoryService.addInventory(data);
    }
    fetchInventory();
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Inventory Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Product</button>

      <table className="donor-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Unit</th>
            <th>Min Inventory</th>
            <th>Max Inventory</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td>{item.productName}</td>
              <td>{item.category}</td>
              <td>{item.unit}</td>
              <td>{item.minInventory}</td>
              <td>{item.maxInventory}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(item)}>✏️ Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(item._id)}>🗑️ Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <h3>{formData._id ? "Edit Product" : "Add Product"}</h3>

            <form onSubmit={handleSubmit} className="responsive-form" encType="multipart/form-data">
              <label>Name of the Product:</label>
              <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required /> 

              <label>Category of Product:</label>
              <input type="text" name="category" value={formData.category || ""} onChange={handleChange} required />

              <label>Unit (No./Kg/Mtr):</label>
              <input type="text" name="unit" value={formData.unit || ""} onChange={handleChange} required />

              <label>Storage Location:</label>
              <input type="text" name="storageLocation" value={formData.storageLocation || ""} onChange={handleChange} />

              <label>Minimum Inventory:</label>
              <input type="number" name="minInventory" value={formData.minInventory || ""} onChange={handleChange} required />

              <label>Maximum Inventory:</label>
              <input type="number" name="maxInventory" value={formData.maxInventory || ""} onChange={handleChange} />

              <label>Photo:</label>
              <input type="file" name="photo" onChange={handleChange} />

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryMaster;
