import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    setCategories(data);
  };

  const handleAddNew = () => {
    setFormData({
      productName: "",
      categoryType: "Primary",
      parentCategory: "",
    });
    setPopupOpen(true);
  };

  const handleEdit = (category) => {
    setFormData({
      _id: category._id,
      productName: category.productName || "",
      categoryType: category.categoryType || "Primary",
      parentCategory: category.parentCategory || "",
    });
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await categoryService.deleteCategory(id);
    fetchCategories();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formData._id) {
        await categoryService.updateCategory(formData._id, formData);
      } else {
        await categoryService.addCategory(formData);
      }
      setPopupOpen(false);
      fetchCategories();
    } catch (error) {
      console.error("Error while submitting the form:", error.response?.data || error);
      alert("An error occurred while submitting the form.");
    }
  };

  // Helper to get parent's name by id for display
  const getParentName = (parentId) => {
    const parent = categories.find((cat) => cat._id === parentId);
    return parent ? parent.productName : "N/A";
  };

  return (
    <div className="master-container">
      <h2>Category Master</h2>
      <button className="add-btn" onClick={handleAddNew}>
        + Add Product
      </button>

      <table className="category-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category Type</th>
            <th>Parent Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((item) => (
            <tr key={item._id}>
              <td>{item.productName || "N/A"}</td>
              <td>{item.categoryType || "N/A"}</td>
              <td>
                {item.categoryType === "Sub" ? getParentName(item.parentCategory) : "—"}
              </td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(item)}>
                  Edit
                </button>
                <button className="delete-btn" onClick={() => handleDelete(item._id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>
              ×
            </button>
            <form onSubmit={handleSubmit}>
              <label>Product Name:</label>
              <input
                type="text"
                name="productName"
                value={formData.productName || ""}
                onChange={handleChange}
                required
              />

              <label>Category Type:</label>
              <select
                name="categoryType"
                value={formData.categoryType || "Primary"}
                onChange={handleChange}
                required
              >
                <option value="Primary">Primary</option>
                <option value="Sub">Sub Category</option>
              </select>

              {formData.categoryType === "Sub" && (
                <>
                  <label>Select Parent Category:</label>
                  <select
                    name="parentCategory"
                    value={formData.parentCategory}
                    onChange={handleChange}
                    required
                  >
                    <option value="">--Select Parent Category--</option>
                    {categories
                      .filter((cat) => cat.categoryType === "Primary")
                      .map((cat) => (
                        <option key={cat._id} value={cat._id}>
                          {cat.productName}
                        </option>
                      ))}
                  </select>
                </>
              )}

              <button className="submit-btn" type="submit">
                Save
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
