import React, { useState, useEffect } from "react";
import maharajService from "../services/maharajService";
import "../styles/MaharajMaster.css"; // <-- or your chosen filename

// Recursively build a nested tree from a flat list
function buildTree(maharajs, parentId = null) {
  return maharajs
    .filter((m) => String(m.parentMaharaj) === String(parentId))
    .map((m) => ({
      ...m,
      children: buildTree(maharajs, m._id),
    }));
}

const MaharajMaster = () => {
  const [maharajs, setMaharajs] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  // Only name & parentMaharaj are used
  const [formData, setFormData] = useState({
    _id: null,
    name: "",
    parentMaharaj: "",
  });

  useEffect(() => {
    fetchMaharajs();
  }, []);

  const fetchMaharajs = async () => {
    try {
      const data = await maharajService.getMaharajs();
      setMaharajs(data);
    } catch (error) {
      console.error("Error fetching maharajs:", error);
    }
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      name: "",
      parentMaharaj: "",
    });
    setIsPopupOpen(true);
  };

  const handleEdit = (maharaj) => {
    setFormData({
      _id: maharaj._id,
      name: maharaj.name,
      parentMaharaj: maharaj.parentMaharaj || "",
    });
    setIsPopupOpen(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this Maharaj?")) return;
    try {
      await maharajService.deleteMaharaj(id);
      fetchMaharajs();
    } catch (error) {
      alert(error.response?.data?.message || "Error deleting Maharaj");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formData._id) {
        await maharajService.updateMaharaj(formData._id, formData);
      } else {
        await maharajService.addMaharaj(formData);
      }
      fetchMaharajs();
      setIsPopupOpen(false);
    } catch (error) {
      alert(error.response?.data?.message || "Error saving Maharaj");
    }
  };

  // Build the nested tree
  const treeData = buildTree(maharajs);

  // Recursively render table rows with indentation
  const renderRows = (nodes, level = 0) => {
    return nodes.flatMap((node) => {
      // Indentation: e.g., for level=0 -> "+", for level=1 -> "----->"
      const indent = level === 0 ? "+ " : " ".repeat(level * 4) + "-> ";
      return [
        <tr key={node._id}>
          <td>
            {indent}
            {node.name}
          </td>
          <td>
            <button className="action-btn" onClick={() => handleEdit(node)}>
              ✏️ Edit
            </button>
            <button className="delete-btn" onClick={() => handleDelete(node._id)}>
              🗑️ Delete
            </button>
          </td>
        </tr>,
        ...renderRows(node.children, level + 1),
      ];
    });
  };

  return (
    <div className="maharaj-container">
      <h2 className="maharaj-heading">Maharaj Master</h2>
      <button className="add-btn" onClick={handleAddNew}>
        + Add Maharaj
      </button>

      <table className="maharaj-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>{renderRows(treeData)}</tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setIsPopupOpen(false)}>
              &times;
            </button>
            <h3>{formData._id ? "Edit Maharaj" : "Add Maharaj"}</h3>
            <form onSubmit={handleSubmit} className="responsive-form">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={formData.name || ""}
                onChange={handleChange}
                required
              />

              <label>Under (Parent Maharaj):</label>
              <select
                name="parentMaharaj"
                value={formData.parentMaharaj || ""}
                onChange={handleChange}
              >
                <option value="">Primary</option>
                {maharajs
                  .filter((m) => m._id !== formData._id) // exclude self
                  .map((m) => (
                    <option key={m._id} value={m._id}>
                      {m.name}
                    </option>
                  ))}
              </select>

              <button className="submit-btn" type="submit">
                Save
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default MaharajMaster;
