import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/authService';
import '../styles/Login.css'; // Import CSS
import axios from 'axios';

function Login() {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [role, setRole] = useState('user'); // 'user' or 'admin'
    const [centers, setCenters] = useState([]); // list of centres
    const [selectedCenter, setSelectedCenter] = useState('');
    const [message, setMessage] = useState(''); // Message for errors or success
    const navigate = useNavigate();

    // When role is "user", fetch centers
    useEffect(() => {
        if (role === 'user') {
            axios.get('/api/centers')
                .then(response => setCenters(response.data))
                .catch(err => console.error("Error fetching centers:", err));
        }
    }, [role]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleRoleChange = (e) => {
        setRole(e.target.value);
        // Reset center if role changes
        if (e.target.value === 'admin') {
            setSelectedCenter('');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const loginData = { ...formData, role };
        if (role === 'user') {
            // For non-admin users, include the selected center
            if (!selectedCenter) {
                setMessage('Please select a Vayavach Centre.');
                return;
            }
            loginData.center = selectedCenter;
        }
        try {
            const response = await login(loginData);
            setMessage('Login successful!');
            // (Optionally store token and role info)
            localStorage.setItem('token', response.token);
            // Redirect based on role â€“ you might load a different dashboard/menu
            navigate('/dashboard');
        } catch (error) {
            setMessage(error.response?.data?.message || 'Login failed');
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Login</h2>
                <div className="role-selection">
                    <label>
                        <input
                            type="radio"
                            name="role"
                            value="admin"
                            checked={role === 'admin'}
                            onChange={handleRoleChange}
                        />
                        Admin
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="role"
                            value="user"
                            checked={role === 'user'}
                            onChange={handleRoleChange}
                        />
                        User
                    </label>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <input
                            type="password"
                            name="password"
                            placeholder="Password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    {role === 'user' && (
                        <div className="input-group">
                            <select
                                name="center"
                                value={selectedCenter}
                                onChange={(e) => setSelectedCenter(e.target.value)}
                                required
                            >
                                <option value="">Select Centre</option>
                                {centers.map(center => (
                                    <option key={center.code} value={center.code}>
                                        {center.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    )}
                    <button type="submit" className="login-button">Login</button>
                </form>
                {message && <p className="login-message">{message}</p>}
                <p className="login-link">
                    Don't have an account? <a href="/register">Register</a>
                </p>
                <p className="login-link">
                    Forgot Password? <a href="/forgot-password">Forgot Password?</a>
                </p>    
            </div>
        </div>
    );
}

export default Login;
