import React, { useState, useEffect } from "react";
import unitService from "../services/unitService";
import "../styles/MasterForms.css";

const UnitMaster = () => {
  const [units, setUnits] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);

  // Form fields
  const [formData, setFormData] = useState({
    _id: null,
    type: "",
    symbol: "",
    formalName: "",
    decimalPlaces: 0,
  });

  useEffect(() => {
    fetchUnits();
  }, []);

  const fetchUnits = async () => {
    try {
      const data = await unitService.getUnits();
      setUnits(data);
    } catch (err) {
      console.error("Error fetching units:", err);
    }
  };

  // Open "Add" form
  const handleAddNew = () => {
    setFormData({
      _id: null,
      type: "",
      symbol: "",
      formalName: "",
      decimalPlaces: 0,
    });
    setPopupOpen(true);
  };

  // Open "Edit" form
  const handleEdit = (unit) => {
    setFormData({
      _id: unit._id,
      type: unit.type,
      symbol: unit.symbol,
      formalName: unit.formalName,
      decimalPlaces: unit.decimalPlaces,
    });
    setPopupOpen(true);
  };

  // Delete
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this unit?")) return;
    try {
      await unitService.deleteUnit(id);
      fetchUnits();
    } catch (err) {
      console.error("Error deleting unit:", err);
    }
  };

  // Form change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      type: formData.type,
      symbol: formData.symbol,
      formalName: formData.formalName,
      decimalPlaces: parseInt(formData.decimalPlaces, 10) || 0,
    };

    try {
      if (formData._id) {
        await unitService.updateUnit(formData._id, payload);
      } else {
        await unitService.addUnit(payload);
      }
      fetchUnits();
      setPopupOpen(false);
    } catch (err) {
      console.error("Error saving unit:", err);
      alert("Failed to save unit. See console for details.");
    }
  };

  return (
    <div className="master-container">
      <h2>Unit Master</h2>
      <button className="add-btn" onClick={handleAddNew}>
        + Add Unit
      </button>

      <table className="donor-table">
        <thead>
          <tr>
            <th>Type</th>
            <th>Symbol</th>
            <th>Formal Name</th>
            <th>Decimal Places</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {units.map((u) => (
            <tr key={u._id}>
              <td>{u.type}</td>
              <td>{u.symbol}</td>
              <td>{u.formalName}</td>
              <td>{u.decimalPlaces}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(u)}>
                  Edit
                </button>
                <button className="delete-btn" onClick={() => handleDelete(u._id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Popup */}
      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>
              ‚ùå
            </button>
            <h3>{formData._id ? "Edit Unit" : "Add Unit"}</h3>
            <form onSubmit={handleSubmit} className="responsive-form">
              <label>Type:</label>
              <input
                type="text"
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
              />

              <label>Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
                required
              />

              <label>Formal Name:</label>
              <input
                type="text"
                name="formalName"
                value={formData.formalName}
                onChange={handleChange}
                required
              />

              <label>Decimal Places:</label>
              <input
                type="number"
                name="decimalPlaces"
                value={formData.decimalPlaces}
                onChange={handleChange}
                required
              />

              <div className="full-width">
                <button type="submit" className="submit-btn">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UnitMaster;
