import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

/**
 * Recursively builds a nested tree from the flat list of categories.
 */
function buildTree(categories, parentId = null) {
  return categories
    .filter((cat) => String(cat.parentCategory) === String(parentId))
    .map((cat) => ({
      ...cat,
      children: buildTree(categories, cat._id),
    }));
}

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);

  // Form fields
  const [formData, setFormData] = useState({
    _id: null,
    productName: "",
    alias: "",
    parentCategory: "",
  });

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    setCategories(data);
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      productName: "",
      alias: "",
      parentCategory: "",
    });
    setPopupOpen(true);
  };

  const handleEdit = (category) => {
    setFormData({
      _id: category._id,
      productName: category.productName,
      alias: category.alias || "",
      parentCategory: category.parentCategory || "",
    });
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    try {
      await categoryService.deleteCategory(id);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error deleting category");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      productName: formData.productName,
      alias: formData.alias,
      parentCategory: formData.parentCategory === "" ? null : formData.parentCategory,
    };

    try {
      if (formData._id) {
        await categoryService.updateCategory(formData._id, payload);
      } else {
        await categoryService.addCategory(payload);
      }
      setPopupOpen(false);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error submitting form");
    }
  };

  // Build the nested tree from categories
  const treeData = buildTree(categories);

  /**
   * Recursively render <tr> rows for each node in the tree.
   * @param {Array} data - Array of category nodes (with .children).
   * @param {number} level - Depth in the tree (for indentation).
   * @returns {Array} Array of <tr> elements.
   */
  const renderRows = (data, level = 0) => {
    return data.flatMap((node) => {
      return [
        // Current node row
        <tr key={node._id}>
          {/* Column 1: Tree structure (indented and fixed width) */}
          <td
            className="tree-td"
            style={{
              paddingLeft: `${level * 20}px`,
              borderLeft: level > 0 ? "1px dashed #ccc" : "none",
            }}
          >
            {node.productName}
            {node.alias ? ` (${node.alias})` : ""}
          </td>

          {/* Column 2: Edit button */}
          <td className="icon-td">
            <button className="icon-button" onClick={() => handleEdit(node)}>
              <i className="fa-solid fa-pen"></i>
            </button>
          </td>

          {/* Column 3: Delete button */}
          <td className="icon-td">
            <button className="icon-button" onClick={() => handleDelete(node._id)}>
              <i className="fa-solid fa-trash"></i>
            </button>
          </td>
        </tr>,

        // Recursively render child rows
        ...renderRows(node.children, level + 1),
      ];
    });
  };

  return (
    <div className="master-container">
      <h2>Category Master</h2>

      <button className="add-btn" onClick={handleAddNew}>
        + Add Category
      </button>

      {/* Scrollable container for the table */}
      <div className="table-container">
        <table className="tree-table">
          <colgroup>
            <col style={{ width: "20em" }} /> {/* Tree Column: 40 characters (~20em) */}
            <col style={{ width: "10em" }} /> {/* Edit Column: 20 characters (~10em) */}
            <col style={{ width: "10em" }} /> {/* Delete Column: 20 characters (~10em) */}
          </colgroup>

          <thead>
            <tr>
              <th>Category Tree</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>{renderRows(treeData)}</tbody>
        </table>
      </div>
    </div>
  );
};

export default CategoryMaster;
