import axios from "axios";

const API_URL = "http://localhost:5000/api/inventory";

export const getInventory = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

export const addInventory = async (inventory) => {
    const formData = new FormData();
    formData.append("productName", inventory.productName);
    formData.append("region", inventory.region);
    formData.append("category", inventory.category);
    formData.append("unit", inventory.unit);
    formData.append("storageLocation", inventory.storageLocation);
    formData.append("minInventory", parseInt(inventory.minInventory, 10));  // ✅ Ensure it's a number
    formData.append("maxInventory", parseInt(inventory.maxInventory, 10));  // ✅ Ensure it's a number
  
    if (inventory.photo) {
      formData.append("photo", inventory.photo);
    }
  
    await axios.post("http://localhost:5000/api/inventory", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  };
  
   

export const updateInventory = async (id, item) => {
  await axios.put(`${API_URL}/${id}`, item, { headers: { "Content-Type": "multipart/form-data" } });
};

export const deleteInventory = async (id) => {
  await axios.delete(`${API_URL}/${id}`);
};

export default { getInventory, addInventory, updateInventory, deleteInventory };
