import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    setCategories(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (category) => {
    setFormData(category);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await categoryService.deleteCategory(id);
    fetchCategories();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
  
    if (name === "photo" && files.length > 0) {
      setFormData({ ...formData, photo: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const data = new FormData();
    data.append("categoryName", formData.categoryName);
    data.append("description", formData.description);
    if (formData.photo) {
      data.append("photo", formData.photo);
    }
  
    try {
      await categoryService.addCategory(data);
      fetchCategories();
      setPopupOpen(false);
    } catch (error) {
      console.error("Error while submitting the form:", error);
      alert("An error occurred while submitting the form.");
    }
  };
  

  return (
    <div className="master-container">
      <h2>Category Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Category</button>

      <table className="category-table">
        <thead>
          <tr>
            <th>Primary Category</th>
            <th>Sub Category</th>
            <th>Product</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((c) => (
            <tr key={c._id}>
              <td>{c.primaryCategory}</td>
              <td>{c.subCategory}</td>
              <td>{c.products.map(p => p.name).join(", ")}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(c)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(c._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <form onSubmit={handleSubmit}>
              <label>Primary Category:</label>
              <input type="text" name="primaryCategory" value={formData.primaryCategory || ""} onChange={handleChange} required />

              <label>Sub Category:</label>
              <input type="text" name="subCategory" value={formData.subCategory || ""} onChange={handleChange} required />

              <label>Product Name:</label>
              <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required />

              <label>Product Photo:</label>
              <input type="file" name="photo" onChange={handleChange} />

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
