import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
    const [categories, setCategories] = useState([]);
    const [formData, setFormData] = useState({
        primaryCategory: "",
        subCategory: "",
        productName: "",
        photo: null,
    });
    const [isPopupOpen, setPopupOpen] = useState(false);
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        const data = await categoryService.getCategories();
        setCategories(data);
    };

    const handleAddNew = () => {
        setFormData({
            primaryCategory: "",
            subCategory: "",
            productName: "",
            photo: null,
        });
        setEditingId(null);
        setPopupOpen(true);
    };

    const handleEdit = (category) => {
        setFormData({
            primaryCategory: category.primaryCategory,
            subCategory: category.subCategory,
            productName: category.products?.[0]?.productName || "",
            photo: null, // Will set a new one if uploaded
        });
        setEditingId(category._id);
        setPopupOpen(true);
    };

    const handleDelete = async (id) => {
        await categoryService.deleteCategory(id);
        fetchCategories();
    };

    const handleChange = (e) => {
        const { name, value, files } = e.target;

        if (name === "photo" && files.length > 0) {
            setFormData({ ...formData, photo: files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formPayload = new FormData();
        formPayload.append("primaryCategory", formData.primaryCategory);
        formPayload.append("subCategory", formData.subCategory);
        formPayload.append("productName", formData.productName);

        if (formData.photo) {
            formPayload.append("photo", formData.photo);
        }

        try {
            if (editingId) {
                await categoryService.updateCategory(editingId, formPayload);
            } else {
                await categoryService.addCategory(formPayload);
            }
            fetchCategories();
            setPopupOpen(false);
        } catch (error) {
            console.error("Error while submitting the form:", error);
            alert("An error occurred while submitting the form.");
        }
    };

    return (
        <div className="master-container">
            <h2>Category Master</h2>
            <button className="add-btn" onClick={handleAddNew}>
                + Add Category
            </button>

            <table className="category-table">
                <thead>
                    <tr>
                        <th>Primary Category</th>
                        <th>Sub Category</th>
                        <th>Product Name</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {categories.map((item) => (
                        <tr key={item._id}>
                            <td>{item.primaryCategory || "N/A"}</td>
                            <td>{item.subCategory || "N/A"}</td>
                            <td>{item.products?.[0]?.productName || "N/A"}</td>
                            <td>
                                <button onClick={() => handleEdit(item)}>Edit</button>
                                <button onClick={() => handleDelete(item._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isPopupOpen && (
                <div className="popup-overlay">
                    <div className="popup-container">
                        <button className="close-btn" onClick={() => setPopupOpen(false)}>‚ùå</button>
                        <form onSubmit={handleSubmit}>
                            <input type="text" name="primaryCategory" value={formData.primaryCategory} onChange={handleChange} required />
                            <input type="text" name="subCategory" value={formData.subCategory} onChange={handleChange} required />
                            <input type="text" name="productName" value={formData.productName} onChange={handleChange} required />
                            <input type="file" name="photo" onChange={handleChange} />
                            <button type="submit">Save</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CategoryMaster;
