import React, { useState, useEffect } from "react";
import { createDonor, updateDonor } from "../api";
import { Button } from "@/components/ui/button";

const DonorForm = ({ donor, onClose }) => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    mobile: "",
    address: "",
  });

  useEffect(() => {
    if (donor) setFormData(donor);
  }, [donor]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (donor) {
      await updateDonor(donor._id, formData);
    } else {
      await createDonor(formData);
    }
    onClose();
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 border rounded-lg bg-white shadow-md">
      <h3 className="text-xl font-bold mb-4">{donor ? "Edit Donor" : "Add Donor"}</h3>

      <label className="block mb-2">Full Name</label>
      <input name="fullName" value={formData.fullName} onChange={handleChange} className="w-full border p-2 mb-4" required />

      <label className="block mb-2">Email</label>
      <input name="email" value={formData.email} onChange={handleChange} className="w-full border p-2 mb-4" type="email" required />

      <label className="block mb-2">Mobile</label>
      <input name="mobile" value={formData.mobile} onChange={handleChange} className="w-full border p-2 mb-4" type="tel" required />

      <label className="block mb-2">Address</label>
      <textarea name="address" value={formData.address} onChange={handleChange} className="w-full border p-2 mb-4" />

      <div className="flex justify-end">
        <Button type="submit" className="bg-green-500">{donor ? "Update" : "Create"}</Button>
        <Button onClick={onClose} className="ml-2 bg-gray-400">Cancel</Button>
      </div>
    </form>
  );
};

export default DonorForm;
