import React from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const role = localStorage.getItem("role") || "user"; 
  // Defaults to "user" if nothing is in localStorage

  return (
    <div className="dashboard-container">
      <h1>Master Dashboard</h1>
      {/* Common buttons for all users */}
      <button onClick={() => navigate("/donor-master")}>Donor Master</button>
      <button onClick={() => navigate("/maharaj-master")}>Maharaj Master</button>
      <button onClick={() => navigate("/inventory-master")}>Inventory Master</button>
      <button onClick={() => navigate("/category-master")}>Category Master</button>
      <button onClick={() => navigate("/donation-center-master")}>Donation Center Master</button>
      <button onClick={() => navigate("/donation-mode-master")}>Donation Mode Master</button>
      <button onClick={() => navigate("/unit-master")}>Unit Master</button>

      {/* Extra admin-only buttons */}
      {role === "admin" && (
        <>
          <button onClick={() => navigate("/center-form")}>Add Centre</button>
          <button onClick={() => navigate("/register-user")}>Register New User</button>
        </>
      )}
    </div>
  );
};

export default Dashboard;
