import React, { useState, useEffect } from "react";
import donorService from "../services/donorService";
import "../styles/MasterForms.css";

const DonorMaster = () => {
  const [donors, setDonors] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    const data = await donorService.getDonors();
    setDonors(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (donor) => {
    setFormData(donor);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await donorService.deleteDonor(id);
    fetchDonors();
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData._id) {
      await donorService.updateDonor(formData._id, formData);
    } else {
      await donorService.addDonor(formData);
    }
    fetchDonors();
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Donor Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Donor</button>

      <table>
        <thead>
          <tr><th>Name</th><th>Email</th><th>Mobile</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {donors.map((d) => (
            <tr key={d._id}>
              <td>{d.fullName}</td>
              <td>{d.email}</td>
              <td>{d.mobile}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(d)}>✏️ Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(d._id)}>🗑️ Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <h3>{formData._id ? "Edit Donor" : "Add Donor"}</h3>
            <form onSubmit={handleSubmit} className="responsive-form">
              <div className="form-group">
                <label>Full Name:</label>
                <input type="text" name="fullName" value={formData.fullName || ""} onChange={handleChange} required />

                <label>Mobile No:</label>
                <input type="tel" name="mobile" value={formData.mobile || ""} onChange={handleChange} pattern="[0-9]{10}" required />

                <label>WhatsApp Mobile No:</label>
                <input type="tel" name="whatsapp" value={formData.whatsapp || ""} onChange={handleChange} pattern="[0-9]{10}" />

                <label>PAN No:</label>
                <input type="text" name="pan" value={formData.pan || ""} onChange={handleChange} pattern="[A-Z]{5}[0-9]{4}[A-Z]{1}" />
              </div>

              <div className="form-group">
                <label>Complete Postal Address:</label>
                <textarea name="address" value={formData.address || ""} onChange={handleChange} required />

                <label>Email ID:</label>
                <input type="email" name="email" value={formData.email || ""} onChange={handleChange} required />

                <label>Reference through Donor made Donation:</label>
                <input type="text" name="ref" value={formData.ref || ""} onChange={handleChange} />

                <label>Preferred Donation Centre:</label>
                <input type="text" name="donationCentre" value={formData.donationCentre || ""} onChange={handleChange} />
              </div>

              <div className="date-group">
                <label>Donor Birth Date:</label>
                <input type="date" name="birthDate" value={formData.birthDate || ""} onChange={handleChange} />

                <label>Wife's Name:</label>
                <input type="text" name="wifeName" value={formData.wifeName || ""} onChange={handleChange} />

                <label>Wife's Birth Date:</label>
                <input type="date" name="wifeBirthDate" value={formData.wifeBirthDate || ""} onChange={handleChange} />

                <label>Anniversary Date:</label>
                <input type="date" name="anniversary" value={formData.anniversary || ""} onChange={handleChange} />
              </div>

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonorMaster;
