import React, { useState, useEffect } from "react";
import maharajService from "../services/maharajService";
import "../styles/maharaj.css";

// Recursively build a nested tree from a flat list
function buildTree(maharajs, parentId = null) {
  return maharajs
    .filter((m) => String(m.parentMaharaj) === String(parentId))
    .map((m) => ({
      ...m,
      children: buildTree(maharajs, m._id),
    }));
}

const MaharajMaster = () => {
  const [maharajs, setMaharajs] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);

  // Form data includes a parentMaharaj field for hierarchy
  const [formData, setFormData] = useState({
    _id: null,
    name: "",
    guruName: "",
    sanghadaName: "",
    cameFrom: "",
    planningToGo: "",
    parentMaharaj: "",
  });

  useEffect(() => {
    fetchMaharajs();
  }, []);

  const fetchMaharajs = async () => {
    try {
      const data = await maharajService.getMaharajs();
      setMaharajs(data);
    } catch (error) {
      console.error("Error fetching maharajs:", error);
    }
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      name: "",
      guruName: "",
      sanghadaName: "",
      cameFrom: "",
      planningToGo: "",
      parentMaharaj: "",
    });
    setPopupOpen(true);
  };

  const handleEdit = (maharaj) => {
    setFormData({
      _id: maharaj._id,
      name: maharaj.name,
      guruName: maharaj.guruName || "",
      sanghadaName: maharaj.sanghadaName || "",
      cameFrom: maharaj.cameFrom || "",
      planningToGo: maharaj.planningToGo || "",
      parentMaharaj: maharaj.parentMaharaj || "",
    });
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    try {
      await maharajService.deleteMaharaj(id);
      fetchMaharajs();
    } catch (error) {
      alert(error.response?.data?.message || "Error deleting Maharaj");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formData._id) {
        await maharajService.updateMaharaj(formData._id, formData);
      } else {
        await maharajService.addMaharaj(formData);
      }
      fetchMaharajs();
      setPopupOpen(false);
    } catch (error) {
      alert(error.response?.data?.message || "Error saving Maharaj");
    }
  };

  // Build the nested tree for display
  const treeData = buildTree(maharajs);

  // Recursively render table rows with indentation for children
  const renderRows = (data, level = 0) => {
    return data.flatMap((node) => {
      const indent = "   ".repeat(level) + (level > 0 ? "└ " : "");
      const displayName = indent + node.name;
      return [
        <tr key={node._id}>
          <td className="indent">{displayName}</td>
          <td>{node.guruName}</td>
          <td>{node.sanghadaName}</td>
          <td>
            <button className="action-btn" onClick={() => handleEdit(node)}>✏️ Edit</button>
            <button className="delete-btn" onClick={() => handleDelete(node._id)}>🗑️ Delete</button>
          </td>
        </tr>,
        ...renderRows(node.children, level + 1)
      ];
    });
  };

  return (
    <div className="maharaj-container">
      <h2 className="maharaj-heading">Maharaj Saheb Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Maharaj</button>
      <table className="maharaj-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Guru Name</th>
            <th>Sanghada Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>{renderRows(treeData)}</tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <h3>{formData._id ? "Edit Maharaj" : "Add Maharaj"}</h3>
            <form onSubmit={handleSubmit} className="responsive-form">
              <label>Name of the Maharajsaheb:</label>
              <input type="text" name="name" value={formData.name || ""} onChange={handleChange} required />

              <label>Their Guru Name:</label>
              <input type="text" name="guruName" value={formData.guruName || ""} onChange={handleChange} required />

              <label>Their Sanghada Name:</label>
              <input type="text" name="sanghadaName" value={formData.sanghadaName || ""} onChange={handleChange} />

              <label>Came From:</label>
              <input type="text" name="cameFrom" value={formData.cameFrom || ""} onChange={handleChange} />

              <label>Planning Further to Go:</label>
              <input type="text" name="planningToGo" value={formData.planningToGo || ""} onChange={handleChange} />

              <label>Under (Parent Maharaj):</label>
              <select name="parentMaharaj" value={formData.parentMaharaj || ""} onChange={handleChange}>
                <option value="">Primary</option>
                {maharajs
                  .filter((m) => m._id !== formData._id) // avoid self in dropdown
                  .map((m) => (
                    <option key={m._id} value={m._id}>
                      {m.name}
                    </option>
                  ))}
              </select>

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default MaharajMaster;
