import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import donorService from "../services/donorService";
import "../styles/DonorMaster.css";

const DonorMaster = () => {
  const navigate = useNavigate();
  const [donors, setDonors] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // To control the modal popup
  const [donor, setDonor] = useState({
    fullName: "",
    address: "",
    mobile: "",
    whatsapp: "",
    email: "",
    ref: "",
    pan: "",
    birthDate: "",
    wifeName: "",
    wifeBirthDate: "",
    anniversary: "",
    donationCentre: "",
  });

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    const data = await donorService.getDonors();
    setDonors(data);
  };

  const handleChange = (e) => {
    setDonor({ ...donor, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await donorService.addDonor(donor);
    fetchDonors();
    setDonor({
      fullName: "",
      address: "",
      mobile: "",
      whatsapp: "",
      email: "",
      ref: "",
      pan: "",
      birthDate: "",
      wifeName: "",
      wifeBirthDate: "",
      anniversary: "",
      donationCentre: "",
    });
    setIsModalOpen(false); // Close modal after submission
  };

  const handleDelete = async (id) => {
    await donorService.deleteDonor(id);
    fetchDonors();
  };

  return (
    <div className="donor-container">
      <h2>Donor Master</h2>
      <button onClick={() => navigate("/")}>Back</button>

      <button className="open-modal-btn" onClick={() => setIsModalOpen(true)}>
        + Add Donor
      </button>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Add Donor Details</h3>
            <form onSubmit={handleSubmit}>
              {Object.keys(donor).map((key) => (
                <div key={key} className="form-group">
                  <label>{key.replace(/([A-Z])/g, " $1").trim()}:</label>
                  <input
                    type="text"
                    name={key}
                    value={donor[key]}
                    onChange={handleChange}
                    required
                  />
                </div>
              ))}
              <button type="submit">Save Donor</button>
              <button type="button" onClick={() => setIsModalOpen(false)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}

      <h3>Donor List</h3>
      <div className="donor-list">
        {donors.map((d) => (
          <div key={d._id} className="donor-card">
            <p>
              <strong>{d.fullName}</strong> - {d.mobile} - {d.email}
            </p>
            <button onClick={() => handleDelete(d._id)}>Delete</button>
          </div>
        ))}
      </div>

      <button
        onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}
        className="scroll-top-btn"
      >
        â†‘ Back to Top
      </button>
    </div>
  );
};

export default DonorMaster;
