import React, { useState, useEffect } from "react";
import inventoryService from "../services/inventoryService";
import "../styles/MasterForms.css";

const InventoryMaster = () => {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    productName: '',
    region: '',
    category: '',
    unit: '',
    storageLocation: '',
    minInventory: '',
    maxInventory: '',
    photo: null
  });
  
  
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    const data = await inventoryService.getInventory();
    setInventory(data);
  };

  const handleAddNew = () => {
    setFormData({
      productName: '',
      region: '',
      category: '',
      unit: '',
      storageLocation: '',
      minInventory: '',
      maxInventory: '',
      photo: null
    });
    setPopupOpen(true);
  };
  
  

  const handleEdit = (item) => {
    setFormData({
        _id: item._id,
        productName: item.productName || "",
        region: item.region || "",
        category: item.category || "",
        unit: item.unit || "",
        storageLocation: item.storageLocation || "",
        minInventory: item.minInventory || "",
        maxInventory: item.maxInventory || "",
        photo: null, // Reset file input field
        existingPhoto: item.photo ? `http://localhost:5000/api/inventory/photo/${item.photo}` : null, // ✅ Store URL of existing photo
    });
    setPopupOpen(true);
};

  

  const handleDelete = async (id) => {
    await inventoryService.deleteInventory(id);
    fetchInventory();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
  
    if (name === "photo" && files.length > 0) {
      setFormData({ ...formData, photo: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };
  
  
  
  
  
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("productName", formData.productName);
    formDataToSend.append("region", formData.region);
    formDataToSend.append("category", formData.category);
    formDataToSend.append("unit", formData.unit);
    formDataToSend.append("storageLocation", formData.storageLocation);
    formDataToSend.append("minInventory", parseInt(formData.minInventory, 10));
    formDataToSend.append("maxInventory", parseInt(formData.maxInventory, 10));

    if (formData.photo) {
        formDataToSend.append("photo", formData.photo);
    } else if (formData.existingPhoto) {
        formDataToSend.append("existingPhoto", formData.existingPhoto); // ✅ Keep existing photo
    }

    try {
        if (formData._id) {
            await inventoryService.updateInventory(formData._id, formDataToSend);
        } else {
            await inventoryService.addInventory(formDataToSend);
        }
        fetchInventory();
        setPopupOpen(false);
    } catch (error) {
        console.error("Error while submitting the form:", error.response?.data || error);
        alert("An error occurred while submitting the form.");
    }
};



  
  
  
  
  
  
  
  
  

  return (
    <div className="master-container">
      <h2>Inventory Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Product</button>

      <table className="donor-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Unit</th>
            <th>Min Inventory</th>
            <th>Max Inventory</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
        {inventory.map((item) => (
            <tr key={item._id}>
                <td>{item.productName}</td>
                <td>{item.category}</td>
                <td>{item.unit}</td>
                <td>{item.minInventory}</td>
                <td>{item.maxInventory}</td>
                <td>
                <td>
                  {item.photo && (
                    <img
                      src={`http://localhost:5000/api/inventory/photo/${item.photo}`} // ✅ Use GridFS API
                      alt="Inventory Item"
                      style={{ width: "50px", height: "50px" }}
                    />
                  )}
                </td>

                </td>
                <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item._id)}>Delete</button>
                </td>
            </tr>
            ))}

        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <h3>{formData._id ? "Edit Product" : "Add Product"}</h3>

            <form onSubmit={handleSubmit}>
            <label>Product Name:</label>
            <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required />

            <label>Region:</label>
            <input type="text" name="region" value={formData.region || ""} onChange={handleChange} required />

            <label>Category of Product:</label>
            <input type="text" name="category" value={formData.category || ""} onChange={handleChange} required />

            <label>Unit (No. or Kg or Mtr):</label>
            <input type="text" name="unit" value={formData.unit || ""} onChange={handleChange} required />

            <label>Storage Location:</label>
            <input type="text" name="storageLocation" value={formData.storageLocation || ""} onChange={handleChange} required />

            <label>Minimum Inventory:</label>
            <input type="number" name="minInventory" value={formData.minInventory || ""} onChange={handleChange} required />

            <label>Maximum Inventory:</label>
            <input type="number" name="maxInventory" value={formData.maxInventory || ""} onChange={handleChange} required />

            <label>Upload Photo:</label>
            {formData.existingPhoto && (
                <div>
                    <p>Current Image:</p>
                    <img 
                        src={formData.existingPhoto} 
                        alt="Existing Inventory" 
                        style={{ width: "100px", height: "100px", objectFit: "cover" }} 
                    />
                </div>
            )}
            <input type="file" name="photo" onChange={handleChange} />

            <button type="submit">Save</button>
            </form>

          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryMaster;
