import React, { useState, useEffect } from "react";
import donationCenterService from "../services/donationCenterService";
import "../styles/MasterForms.css";

const DonationCenterMaster = () => {
  const [centers, setCenters] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchCenters();
  }, []);

  const fetchCenters = async () => {
    const data = await donationCenterService.getDonationCenters();
    setCenters(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (center) => {
    setFormData(center);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await donationCenterService.deleteDonationCenter(id);
    fetchCenters();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData._id) {
      await donationCenterService.updateDonationCenter(formData._id, formData);
    } else {
      await donationCenterService.addDonationCenter(formData);
    }
    fetchCenters();
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Donation Center Cum Store Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Donation Center</button>

      <table className="donation-center-table">
        <thead>
          <tr>
            <th>Area</th>
            <th>Store Center Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {centers.map((c) => (
            <tr key={c._id}>
              <td>{c.area}</td>
              <td>{c.centerName}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(c)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(c._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <form onSubmit={handleSubmit}>
              <label>Donation Center / Store Area:</label>
              <input type="text" name="area" value={formData.area || ""} onChange={handleChange} required />

              <label>Store Center Name:</label>
              <input type="text" name="centerName" value={formData.centerName || ""} onChange={handleChange} required />

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonationCenterMaster;
