import axios from "axios";

const API_URL = "http://localhost:5000/api/categories";

export const getCategories = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

export const addCategory = async (category) => {
    const formData = new FormData();
    formData.append("primaryCategory", category.primaryCategory);
    formData.append("subCategory", category.subCategory);
    formData.append("productName", category.productName);
    if (category.photo) {
      formData.append("photo", category.photo);
    }
  
    await axios.post("http://localhost:5000/api/categories", formData, {
      headers: {
        "Content-Type": "multipart/form-data",  // ✅ Ensure correct header
      },
    });
  };
  
export const updateCategory = async (id, category) => {
  await axios.put(`${API_URL}/${id}`, category);
};

export const deleteCategory = async (id) => {
  await axios.delete(`${API_URL}/${id}`);
};

export default { getCategories, addCategory, updateCategory, deleteCategory };
