// export default InventoryMaster;
import React, { useState, useEffect } from "react";
import inventoryService from "../services/inventoryService";
// import categoryService from "../services/categoryService"; // import your Category service
import unitService from "../services/unitService";         // import your Unit service
import "../styles/MasterForms.css";

const InventoryMaster = () => {
  const [inventory, setInventory] = useState([]);
  const [categories, setCategories] = useState([]);
  const [units, setUnits] = useState([]);

  // Form Data
  const [formData, setFormData] = useState({
    _id: null,        // for editing
    code: "",         // auto-generated (read-only in UI)
    name: "",
    category: "",
    unit: "",
    size: "",
    color: "",
    quantity: 0,
    rate: 0,
    value: 0,
    photo: null,
    existingPhoto: null, // for editing
  });

  const [isPopupOpen, setPopupOpen] = useState(false);

  // On mount, fetch inventory + categories + units
  useEffect(() => {
    fetchInventory();
    fetchCategories();
    fetchUnits();
  }, []);

  const fetchInventory = async () => {
    try {
      const data = await inventoryService.getInventory();
      setInventory(data);
    } catch (error) {
      console.error("Error fetching inventory:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const data = await categoryService.getCategories();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchUnits = async () => {
    try {
      const data = await unitService.getUnits();
      setUnits(data);
    } catch (error) {
      console.error("Error fetching units:", error);
    }
  };

  // Open the "Add" popup
  const handleAddNew = () => {
    setFormData({
      _id: null,
      code: "",
      name: "",
      category: "",
      unit: "",
      size: "",
      color: "",
      quantity: 0,
      rate: 0,
      value: 0,
      photo: null,
      existingPhoto: null,
    });
    setPopupOpen(true);
  };

  // Open the "Edit" popup
  const handleEdit = (item) => {
    setFormData({
      _id: item._id,
      code: item.code || "", // read-only in UI
      name: item.name || "",
      category: item.category?._id || "", // or item.category if not populated
      unit: item.unit?._id || "",
      size: item.size || "",
      color: item.color || "",
      quantity: item.quantity || 0,
      rate: item.rate || 0,
      value: item.value || 0,
      photo: null,
      existingPhoto: item.photo
        ? `http://localhost:5000/api/inventory/photo/${item.photo}`
        : null,
    });
    setPopupOpen(true);
  };

  // Delete item
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await inventoryService.deleteInventory(id);
      fetchInventory();
    } catch (error) {
      console.error("Error deleting inventory:", error);
    }
  };

  // Handle form changes
  const handleChange = (e) => {
    const { name, value, files } = e.target;

    // If photo is being changed
    if (name === "photo" && files.length > 0) {
      setFormData((prev) => ({ ...prev, photo: files[0] }));
      return;
    }

    // For quantity or rate, auto-calc value
    if (name === "quantity" || name === "rate") {
      const newState = { ...formData, [name]: parseFloat(value) || 0 };
      newState.value = newState.quantity * newState.rate;
      setFormData(newState);
      return;
    }

    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Build FormData for file upload
    const fd = new FormData();
    // We'll only send `code` if editing, since code is auto-generated on create
    if (formData._id) {
      fd.append("code", formData.code);
    }
    fd.append("name", formData.name);
    fd.append("category", formData.category);
    fd.append("unit", formData.unit);
    fd.append("size", formData.size);
    fd.append("color", formData.color);
    fd.append("quantity", formData.quantity);
    fd.append("rate", formData.rate);
    fd.append("value", formData.value);

    if (formData.photo) {
      fd.append("photo", formData.photo);
    } else if (formData.existingPhoto) {
      fd.append("existingPhoto", formData.existingPhoto);
    }

    try {
      if (formData._id) {
        // Update
        await inventoryService.updateInventory(formData._id, fd);
      } else {
        // Create
        await inventoryService.addInventory(fd);
      }
      fetchInventory();
      setPopupOpen(false);
    } catch (error) {
      console.error("Error submitting inventory form:", error);
      alert("An error occurred while saving inventory.");
    }
  };

  return (
    <div className="master-container">
      <h2>Inventory Master</h2>
      <button className="add-btn" onClick={handleAddNew}>
        + Add Product
      </button>

      {/* Table of items */}
      <table className="donor-table">
        <thead>
          <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Category</th>
            <th>Unit</th>
            <th>Size</th>
            <th>Color</th>
            <th>Quantity</th>
            <th>Rate</th>
            <th>Value</th>
            <th>Photo</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td>{item.code}</td>
              <td>{item.name}</td>
              <td>{item.category?.productName || "—"}</td>
              <td>{item.unit?.symbol || "—"}</td>
              <td>{item.size}</td>
              <td>{item.color}</td>
              <td>
                {item.quantity}{" "}
                {item.unit?.symbol ? item.unit.symbol : ""}
              </td>
              <td>{item.rate}</td>
              <td>{item.value}</td>
              <td>
                {item.photo && (
                  <img
                    src={`http://localhost:5000/api/inventory/photo/${item.photo}`}
                    alt="item"
                    style={{ width: "50px", height: "50px", objectFit: "cover" }}
                  />
                )}
              </td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(item)}>
                  Edit
                </button>
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(item._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Popup Form */}
      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>
              ❌
            </button>
            <h3>{formData._id ? "Edit Product" : "Add Product"}</h3>

            <form onSubmit={handleSubmit} className="responsive-form">
              {/* Code (Read-Only if editing) */}
              {formData._id && (
                <>
                  <label>Code:</label>
                  <input
                    type="text"
                    name="code"
                    value={formData.code}
                    onChange={() => {}}
                    readOnly
                  />
                </>
              )}

              {/* Name */}
              <label>Name of Item:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              {/* Category */}
              <label>Category:</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">-- Select Category --</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.productName}
                  </option>
                ))}
              </select>

              {/* Unit */}
              <label>Unit:</label>
              <select
                name="unit"
                value={formData.unit}
                onChange={handleChange}
                required
              >
                <option value="">-- Select Unit --</option>
                {units.map((u) => (
                  <option key={u._id} value={u._id}>
                    {u.symbol} ({u.formalName})
                  </option>
                ))}
              </select>

              {/* Size */}
              <label>Size:</label>
              <input
                type="text"
                name="size"
                value={formData.size}
                onChange={handleChange}
              />

              {/* Color */}
              <label>Color:</label>
              <input
                type="text"
                name="color"
                value={formData.color}
                onChange={handleChange}
              />

              {/* Quantity */}
              <label>Quantity:</label>
              <input
                type="number"
                step="any"
                name="quantity"
                value={formData.quantity}
                onChange={handleChange}
              />

              {/* Rate */}
              <label>Rate per item:</label>
              <input
                type="number"
                step="any"
                name="rate"
                value={formData.rate}
                onChange={handleChange}
              />

              {/* Value (read-only if you want, or let them override) */}
              <label>Value:</label>
              <input
                type="number"
                step="any"
                name="value"
                value={formData.value}
                onChange={handleChange}
              />

              {/* Photo */}
              <label>Photo:</label>
              {formData.existingPhoto && (
                <div>
                  <img
                    src={formData.existingPhoto}
                    alt="Existing"
                    style={{ width: "100px", height: "100px", objectFit: "cover" }}
                  />
                </div>
              )}
              <input type="file" name="photo" onChange={handleChange} />

              {/* Submit */}
              <div className="full-width">
                <button type="submit" className="submit-btn">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryMaster;
