import React, { useState } from "react";
import DonorList from "./DonorList";
import DonorForm from "./DonorForm";
import { Button } from "@/components/ui/button";

const DonorMaster = () => {
  const [selectedDonor, setSelectedDonor] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleAddNew = () => {
    setSelectedDonor(null); // Reset form
    setShowForm(true);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Donor Master</h2>
      {showForm ? (
        <DonorForm donor={selectedDonor} onClose={() => setShowForm(false)} />
      ) : (
        <>
          <Button onClick={handleAddNew} className="mb-4">
            + Add Donor
          </Button>
          <DonorList onEdit={setSelectedDonor} onShowForm={setShowForm} />
        </>
      )}
    </div>
  );
};

export default DonorMaster;
