import axios from "axios";

const API_URL = "http://localhost:5000/api/donors";  // Adjust API URL if needed

const getDonors = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

const addDonor = async (donor) => {
  await axios.post(API_URL, donor);
};

const updateDonor = async (id, donor) => {
  await axios.put(`${API_URL}/${id}`, donor);  // Update API call
};

const deleteDonor = async (id) => {
  await axios.delete(`${API_URL}/${id}`);  // Delete API call
};

export default { getDonors, addDonor, updateDonor, deleteDonor };
