import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    console.log("Fetched Categories:", data); // ✅ Debugging
    setCategories(data);
  };
  

  const handleAddNew = () => {
    setFormData({
      primaryCategory: "",
      subCategory: "",
      productName: "",
      photo: null,
    });
    setPopupOpen(true);
  };
  
  

  const handleEdit = (category) => {
    setFormData(category);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await categoryService.deleteCategory(id);
    fetchCategories();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
  
    if (name === "photo" && files.length > 0) {
      setFormData({ ...formData, photo: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };
  
  

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const formData = new FormData();
    formData.append("primaryCategory", formData.primaryCategory);
    formData.append("subCategory", formData.subCategory);
    formData.append("productName", formData.productName);
    if (formData.photo) {
      formData.append("photo", formData.photo);
    }
  
    console.log("Form Data:", Object.fromEntries(formData.entries()));  // ✅ Check data before submission
  
    try {
      await categoryService.addCategory(formData);  // ✅ Directly pass FormData
      fetchCategories();
      setPopupOpen(false);
    } catch (error) {
      console.error("Error while submitting the form:", error);
      alert("An error occurred while submitting the form.");
    }
  };
  
  
  
  
  
  

  return (
    <div className="master-container">
      <h2>Category Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Category</button>

      <table className="category-table">
  <thead>
    <tr>
      <th>Primary Category</th>
      <th>Sub Category</th>
      <th>Product Name</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    {categories.map((item) => (
      <tr key={item._id}>
        <td>{item.primaryCategory || "N/A"}</td>
        <td>{item.subCategory || "N/A"}</td>
        <td>{item.products?.[0]?.productName || "N/A"}</td>
        <td>
          <button onClick={() => handleEdit(item)}>Edit</button>
          <button onClick={() => handleDelete(item._id)}>Delete</button>
        </td>
      </tr>
    ))}
  </tbody>
</table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <form onSubmit={handleSubmit}>
              <label>Primary Category:</label>
              <input type="text" name="primaryCategory" value={formData.primaryCategory || ""} onChange={handleChange} required />

              <label>Sub Category:</label>
              <input type="text" name="subCategory" value={formData.subCategory || ""} onChange={handleChange} required />

              <label>Product Name:</label>
              <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required />

              <label>Product Photo:</label>
              <input type="file" name="photo" onChange={handleChange} />

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
