// export default CategoryMaster;
const express = require("express");
const router = express.Router();
const Category = require("../models/category");

// Ensure the app parses JSON bodies
router.use(express.json());

// GET all categories
router.get("/", async (req, res) => {
  try {
    const categories = await Category.find();
    res.json(categories);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});

// ADD new category
router.post("/", async (req, res) => {
  try {
    console.log("Received request:", req.body);
    // No file upload, so no logging for file

    const { productName, categoryType, parentCategory } = req.body;
    if (!productName || !categoryType) {
      return res.status(400).json({ message: "Product Name and Category Type are required." });
    }
    // For sub categories, ensure a parent category is provided
    if (categoryType === "Sub" && !parentCategory) {
      return res.status(400).json({ message: "Parent Category is required for Sub Category." });
    }

    const newCategory = new Category({
      productName,
      categoryType,
      parentCategory: categoryType === "Sub" ? parentCategory : null,
    });

    await newCategory.save();
    res.status(201).json(newCategory);
  } catch (error) {
    console.error("Error in POST /api/categories:", error);
    res.status(500).json({ message: error.message });
  }
});

// UPDATE category
router.put("/:id", async (req, res) => {
  try {
    console.log("Incoming Update Request:", req.body);

    const { productName, categoryType, parentCategory } = req.body;

    const category = await Category.findById(req.params.id);
    if (!category) return res.status(404).json({ message: "Category not found" });

    if (categoryType === "Sub" && !parentCategory) {
      return res.status(400).json({ message: "Parent Category is required for Sub Category." });
    }

    category.productName = productName;
    category.categoryType = categoryType;
    category.parentCategory = categoryType === "Sub" ? parentCategory : null;

    const updatedCategory = await category.save();
    res.json(updatedCategory);
  } catch (error) {
    console.error("Update Error:", error);
    res.status(500).json({ message: error.message });
  }
});

// DELETE category
router.delete("/:id", async (req, res) => {
  try {
    await Category.findByIdAndDelete(req.params.id);
    res.json({ message: "Category deleted successfully" });
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});

module.exports = router;
