import React, { useState } from "react";
import CommonPopup from "./CommonPopup";
import "../styles/MasterForms.css";

const MaharajMaster = () => {
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Saved Maharaj:", formData);
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Maharaj Saheb Master</h2>
      <button className="add-btn" onClick={() => setPopupOpen(true)}>+ Add Maharaj</button>

      <CommonPopup isOpen={isPopupOpen} onClose={() => setPopupOpen(false)}>
        <form onSubmit={handleSubmit}>
          <label>Name</label>
          <input type="text" name="name" value={formData.name || ""} onChange={handleChange} required />

          <label>Guru Name</label>
          <input type="text" name="guruName" value={formData.guruName || ""} onChange={handleChange} required />

          <label>Sanghada Name</label>
          <input type="text" name="sanghadaName" value={formData.sanghadaName || ""} onChange={handleChange} required />

          <button type="submit">Save</button>
        </form>
      </CommonPopup>
    </div>
  );
};

export default MaharajMaster;
