import React, { useState, useEffect } from "react";
import inventoryService from "../services/inventoryService";
import categoryService from "../services/categoryService";
import unitService from "../services/unitService";
import "../styles/FinalMasters.css";

const InventoryMaster = () => {
  const [inventory, setInventory] = useState([]);
  const [categories, setCategories] = useState([]);
  const [units, setUnits] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const [formData, setFormData] = useState({
    _id: null,
    code: "",
    name: "",
    category: "",
    unit: "",
    size: "",
    color: "",
    quantity: 0,
    rate: 0,
    value: 0,
    photo: null,
    existingPhoto: null,
  });

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      const invData = await inventoryService.getInventory();
      setInventory(invData);
      const catData = await categoryService.getCategories();
      setCategories(catData);
      const unitData = await unitService.getUnits();
      setUnits(unitData);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      code: "",
      name: "",
      category: "",
      unit: "",
      size: "",
      color: "",
      quantity: 0,
      rate: 0,
      value: 0,
      photo: null,
      existingPhoto: null,
    });
    setShowModal(true);
  };

  const handleEdit = (item) => {
    setFormData({
      _id: item._id,
      code: item.code || "",
      name: item.name || "",
      category: item.category?._id || "",
      unit: item.unit?._id || "",
      size: item.size || "",
      color: item.color || "",
      quantity: item.quantity || 0,
      rate: item.rate || 0,
      value: item.value || 0,
      photo: null,
      existingPhoto: item.photo
        ? `http://localhost:5000/api/inventory/photo/${item.photo}`
        : null,
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await inventoryService.deleteInventory(id);
      fetchAllData();
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "photo" && files.length > 0) {
      setFormData((prev) => ({ ...prev, photo: files[0] }));
    } else if (name === "quantity" || name === "rate") {
      // Ensure the value is numeric and not negative
      const numericVal = Math.max(0, parseFloat(value) || 0);
      const newState = { ...formData, [name]: numericVal };
      newState.value =
        name === "quantity"
          ? newState.rate * numericVal
          : newState.quantity * numericVal;
      setFormData(newState);
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.quantity < 0 || formData.rate < 0) {
      alert("Quantity and Rate cannot be negative.");
      return;
    }
    try {
      const payload = new FormData();
      if (formData._id) payload.append("code", formData.code);
      payload.append("name", formData.name);
      payload.append("category", formData.category);
      payload.append("unit", formData.unit);
      payload.append("size", formData.size);
      payload.append("color", formData.color);
      payload.append("quantity", formData.quantity);
      payload.append("rate", formData.rate);
      payload.append("value", formData.value);

      if (formData.photo) {
        payload.append("photo", formData.photo);
      } else if (formData.existingPhoto) {
        payload.append("existingPhoto", formData.existingPhoto);
      }

      if (formData._id) {
        await inventoryService.updateInventory(formData._id, payload);
      } else {
        await inventoryService.addInventory(payload);
      }
      fetchAllData();
      setShowModal(false);
    } catch (error) {
      console.error("Error saving inventory:", error);
      alert("Failed to save inventory item.");
    }
  };

  // Compute the display string for quantity with unit suffix
  const selectedUnit = units.find(u => u._id === formData.unit);
  const quantityWithSuffix = formData.quantity + (selectedUnit ? " " + selectedUnit.symbol : "");

  return (
    <div className="im-container">
      <h2 className="im-heading">Inventory Master</h2>
      <button className="im-add-btn" onClick={handleAddNew}>
        + Add Product
      </button>

      <table className="im-table">
        <thead>
          <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Under</th>
            <th>Unit</th>
            <th>Size</th>
            <th>Color</th>
            <th>Quantity</th>
            <th>Rate</th>
            <th>Value</th>
            <th>Photo</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td>{item.code}</td>
              <td>{item.name}</td>
              <td>{item.category?.productName || "—"}</td>
              <td>{item.unit?.symbol || "—"}</td>
              <td>{item.size}</td>
              <td>{item.color}</td>
              <td>{item.quantity} {item.unit?.symbol || ""}</td>
              <td>{item.rate}</td>
              <td>{item.value}</td>
              <td>
                {item.photo && (
                  <img
                    src={`http://localhost:5000/api/inventory/photo/${item.photo}`}
                    alt="item"
                    style={{ width: "40px", height: "40px", objectFit: "cover" }}
                  />
                )}
              </td>
              <td>
                <button className="im-edit-btn" onClick={() => handleEdit(item)}>
                  Edit
                </button>
                <button className="im-delete-btn" onClick={() => handleDelete(item._id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for Add/Edit with Tally-like 2-column form layout */}
      {showModal && (
        <div className="im-overlay">
          <div className="im-modal">
            <button className="im-close-btn" onClick={() => setShowModal(false)}>
              &times;
            </button>
            <h3>{formData._id ? "Edit Product" : "Add Product"}</h3>

            <form onSubmit={handleSubmit} className="im-form">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <label>Under:</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">-- Select Category --</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.productName}
                  </option>
                ))}
              </select>

              <label>Unit:</label>
              <select
                name="unit"
                value={formData.unit}
                onChange={handleChange}
                required
              >
                <option value="">-- Select Unit --</option>
                {units.map((u) => (
                  <option key={u._id} value={u._id}>
                    {u.symbol}
                  </option>
                ))}
              </select>

              <label>Size:</label>
              <input
                type="text"
                name="size"
                value={formData.size}
                onChange={handleChange}
              />

              <label>Color:</label>
              <input
                type="text"
                name="color"
                value={formData.color}
                onChange={handleChange}
              />

              <label>Quantity:</label>
              <input
                type="number"
                name="quantity"
                min="0"
                step="any"
                value={formData.quantity}
                onChange={handleChange}
                required
              />
              <div className="im-qty-display" style={{ textAlign: "left", marginBottom: "10px", fontStyle: "italic", color: "#555" }}>
                {quantityWithSuffix}
              </div>

              <label>Rate per item:</label>
              <input
                type="number"
                name="rate"
                min="0"
                step="any"
                value={formData.rate}
                onChange={handleChange}
                required
              />

              <label>Value:</label>
              <input
                type="number"
                name="value"
                min="0"
                step="any"
                value={formData.value}
                onChange={handleChange}
                required
              />

              <label>Photo:</label>
              <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                {formData.existingPhoto && (
                  <img
                    src={formData.existingPhoto}
                    alt="existing"
                    style={{ width: "50px", height: "50px", objectFit: "cover" }}
                  />
                )}
                <input type="file" name="photo" onChange={handleChange} />
              </div>

              <div className="im-form-submit">
                <button type="submit" className="im-save-btn">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryMaster;
