import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    console.log("Fetched Categories:", data); // ✅ Debugging
    setCategories(data);
  };
  

  const handleAddNew = () => {
    setFormData({
      primaryCategory: "",
      subCategory: "",
      productName: "",
      photo: null,
    });
    setPopupOpen(true);
  };
  
  

  const handleEdit = (category) => {
    setFormData({
        _id: category._id,
        primaryCategory: category.primaryCategory || "",
        subCategory: category.subCategory || "",
        productName: category.products?.[0]?.productName || "",
        photo: null, // Reset file input
        existingPhoto: category.products?.[0]?.photo 
            ? `data:${category.products[0].photo.contentType};base64,${btoa(
                String.fromCharCode(...new Uint8Array(category.products[0].photo.data.data))
              )}`
            : null, // ✅ Store existing photo
    });
    setPopupOpen(true);
};



  const handleDelete = async (id) => {
    await categoryService.deleteCategory(id);
    fetchCategories();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
  
    if (name === "photo" && files.length > 0) {
      setFormData({ ...formData, photo: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };
  
  

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("primaryCategory", formData.primaryCategory);
    formDataToSend.append("subCategory", formData.subCategory);
    formDataToSend.append("productName", formData.productName);

    if (formData.photo) {
        formDataToSend.append("photo", formData.photo);
    } else if (formData.existingPhoto) {
        formDataToSend.append("existingPhoto", formData.existingPhoto);
    }

    try {
        if (formData._id) {
            await categoryService.updateCategory(formData._id, formDataToSend);
        } else {
            await categoryService.addCategory(formDataToSend);
        }
        
        // ✅ Avoid unnecessary state updates
        setPopupOpen(false);
        fetchCategories(); // ✅ Ensure this doesn't cause infinite re-renders
    } catch (error) {
        console.error("Error while submitting the form:", error.response?.data || error);
        alert("An error occurred while submitting the form.");
    }
};




  
  
  
  
  
  
  

  return (
    <div className="master-container">
      <h2>Category Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Category</button>

      <table className="category-table">
  <thead>
    <tr>
      <th>Primary Category</th>
      <th>Sub Category</th>
      <th>Product Name</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
  {categories.map((item) => (
    <tr key={item._id}>
      <td>{item.primaryCategory || "N/A"}</td>
      <td>{item.subCategory || "N/A"}</td>
      <td>{item.products?.[0]?.productName || "N/A"}</td>
      <td>
        {item.products?.[0]?.photo?.data && (
          <img
            src={`data:${item.products[0].photo.contentType};base64,${btoa(
              String.fromCharCode(...new Uint8Array(item.products[0].photo.data.data))
            )}`}
            alt="Product"
            style={{ width: "100px", height: "100px" }}
          />
        )}
        <button onClick={() => handleEdit(item)}>Edit</button>
        <button onClick={() => handleDelete(item._id)}>Delete</button>
      </td>
    </tr>
  ))}
</tbody>


</table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <form onSubmit={handleSubmit}>
              <label>Primary Category:</label>
              <input type="text" name="primaryCategory" value={formData.primaryCategory || ""} onChange={handleChange} required />

              <label>Sub Category:</label>
              <input type="text" name="subCategory" value={formData.subCategory || ""} onChange={handleChange} required />

              <label>Product Name:</label>
              <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required />

              <label>Product Photo:</label>
              {formData.existingPhoto && (
                  <div>
                      <p>Current Image:</p>
                      <img 
                          src={formData.existingPhoto} 
                          alt="Existing Product"
                          style={{ width: "100px", height: "100px", objectFit: "cover" }} 
                      />
                  </div>
              )}
              <input type="file" name="photo" onChange={handleChange} />


              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
