import React, { useState, useEffect } from "react";
import inventoryService from "../services/inventoryService";
import "../styles/MasterForms.css";

const InventoryMaster = () => {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    productName: '',
    region: '',
    category: '',
    unit: '',
    storageLocation: '',
    minInventory: '',
    maxInventory: '',
    photo: null
  });
  
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    const data = await inventoryService.getInventory();
    setInventory(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (item) => {
    setFormData(item);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await inventoryService.deleteInventory(id);
    fetchInventory();
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
  
    if (name === "photo") {
      setFormData({ ...formData, photo: files[0] });
    } else if (name === "minInventory" || name === "maxInventory") {
      setFormData({ ...formData, [name]: value === "" ? "" : parseInt(value, 10) });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };
  
  
  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Ensure minInventory and maxInventory are parsed as numbers
    const minInv = parseInt(formData.minInventory, 10);
    const maxInv = parseInt(formData.maxInventory, 10);
  
    // Validation before sending data
    if (
      !formData.productName ||
      !formData.region ||
      !formData.category ||
      !formData.unit ||
      !formData.storageLocation ||
      isNaN(minInv) ||
      isNaN(maxInv)
    ) {
      alert("Error: All fields are required and Min/Max Inventory must be valid numbers.");
      return;
    }
  
    const data = new FormData();
    data.append("productName", formData.productName);
    data.append("region", formData.region);
    data.append("category", formData.category);
    data.append("unit", formData.unit);
    data.append("storageLocation", formData.storageLocation);
    data.append("minInventory", minInv);
    data.append("maxInventory", maxInv);
  
    if (formData.photo) {
      data.append("photo", formData.photo);
    }
  
    try {
      if (formData._id) {
        await inventoryService.updateInventory(formData._id, data);
      } else {
        await inventoryService.addInventory(data);
      }
      fetchInventory();
      setPopupOpen(false);
    } catch (error) {
      console.error("Error while submitting the form:", error);
      alert("An error occurred while submitting the form.");
    }
  };
  
  
  
  
  

  return (
    <div className="master-container">
      <h2>Inventory Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Product</button>

      <table className="donor-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Unit</th>
            <th>Min Inventory</th>
            <th>Max Inventory</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td>{item.productName}</td>
              <td>{item.category}</td>
              <td>{item.unit}</td>
              <td>{item.minInventory}</td>
              <td>{item.maxInventory}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(item)}>‚úèÔ∏è Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(item._id)}>üóëÔ∏è Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>‚ùå</button>
            <h3>{formData._id ? "Edit Product" : "Add Product"}</h3>

            <form onSubmit={handleSubmit}>
            <label>Product Name:</label>
            <input type="text" name="productName" value={formData.productName || ""} onChange={handleChange} required />

            <label>Region:</label>
            <input type="text" name="region" value={formData.region || ""} onChange={handleChange} required />

            <label>Category of Product:</label>
            <input type="text" name="category" value={formData.category || ""} onChange={handleChange} required />

            <label>Unit (No. or Kg or Mtr):</label>
            <input type="text" name="unit" value={formData.unit || ""} onChange={handleChange} required />

            <label>Storage Location:</label>
            <input type="text" name="storageLocation" value={formData.storageLocation || ""} onChange={handleChange} required />

            <label>Minimum Inventory:</label>
            <input type="number" name="minInventory" value={formData.minInventory || ""} onChange={handleChange} required />

            <label>Maximum Inventory:</label>
            <input type="number" name="maxInventory" value={formData.maxInventory || ""} onChange={handleChange} required />

            <label>Upload Photo:</label>
            <input type="file" name="photo" onChange={handleChange} />

            <button type="submit">Save</button>
            </form>

          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryMaster;
