import React, { useEffect, useState } from "react";
import { createDonor, updateDonor, fetchDonors, deleteDonor } from "../api"; 
// Make sure `api.js` is in `src/api.js`

import { Button } from "@/components/ui/button";

const DonorList = ({ onEdit, onShowForm }) => {
  const [donors, setDonors] = useState([]);

  useEffect(() => {
    loadDonors();
  }, []);

  const loadDonors = async () => {
    const data = await fetchDonors();
    setDonors(data);
  };

  const handleDelete = async (id) => {
    await deleteDonor(id);
    loadDonors();
  };

  return (
    <table className="w-full border-collapse border">
      <thead>
        <tr className="bg-gray-100">
          <th className="border p-2">Name</th>
          <th className="border p-2">Email</th>
          <th className="border p-2">Mobile</th>
          <th className="border p-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {donors.map((donor) => (
          <tr key={donor._id} className="text-center border">
            <td className="p-2">{donor.fullName}</td>
            <td className="p-2">{donor.email}</td>
            <td className="p-2">{donor.mobile}</td>
            <td className="p-2">
              <Button onClick={() => { onEdit(donor); onShowForm(true); }}>
                ‚úèÔ∏è Edit
              </Button>
              <Button onClick={() => handleDelete(donor._id)} className="ml-2 bg-red-500">
                üóë Delete
              </Button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DonorList;
