import React, { useState, useEffect } from "react";
import donorService from "../services/donorService";
import CommonPopup from "./CommonPopup";
import "../styles/MasterForms.css";

const DonorMaster = () => {
  const [donors, setDonors] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    const data = await donorService.getDonors();
    setDonors(data);
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (donor) => {
    setFormData(donor);  // Load donor details into the form
    setPopupOpen(true);  // Open popup for editing
  };

  const handleDelete = async (id) => {
    await donorService.deleteDonor(id);  // Call delete service
    fetchDonors();  // Refresh the donor list
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData._id) {
      await donorService.updateDonor(formData._id, formData);  // Update donor
    } else {
      await donorService.addDonor(formData);  // Add new donor
    }
    fetchDonors();
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Donor Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Donor</button>

      <table>
        <thead>
          <tr><th>Name</th><th>Email</th><th>Mobile</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {donors.map((d) => (
            <tr key={d._id}>
              <td>{d.fullName}</td>
              <td>{d.email}</td>
              <td>{d.mobile}</td>
              <td>
                <button onClick={() => handleEdit(d)}>‚úèÔ∏è Edit</button>
                <button onClick={() => handleDelete(d._id)}>üóëÔ∏è Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <CommonPopup isOpen={isPopupOpen} onClose={() => setPopupOpen(false)}>
        <form onSubmit={handleSubmit}>
          <label>Full Name</label>
          <input type="text" name="fullName" value={formData.fullName || ""} onChange={handleChange} required />

          <label>Email</label>
          <input type="email" name="email" value={formData.email || ""} onChange={handleChange} required />

          <label>Mobile</label>
          <input type="tel" name="mobile" value={formData.mobile || ""} onChange={handleChange} required />

          <button type="submit">Save</button>
        </form>
      </CommonPopup>
    </div>
  );
};

export default DonorMaster;
