import React, { useState, useEffect } from "react";
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

// Build a nested tree from the flat list
function buildTree(categories, parentId = null) {
  return categories
    .filter((cat) => String(cat.parentCategory) === String(parentId))
    .map((cat) => ({
      ...cat,
      children: buildTree(categories, cat._id),
    }));
}

// Recursive component to render one category node and its children
function CategoryNode({ node, onEdit, onDelete }) {
  return (
    <li>
      <div className="tree-row">
        {/* Left column: category name & alias */}
        <div className="tree-label-col">
          {node.productName}
          {node.alias ? ` (${node.alias})` : ""}
        </div>
        {/* Right column: action buttons (edit/delete) */}
        <div className="tree-actions-col">
          <button className="edit-btn" onClick={() => onEdit(node)}>
            ‚úèÔ∏è
          </button>
          <button className="delete-btn" onClick={() => onDelete(node._id)}>
            üóëÔ∏è
          </button>
        </div>
      </div>
      {/* Render children if any */}
      {node.children.length > 0 && (
        <ul>
          {node.children.map((child) => (
            <CategoryNode
              key={child._id}
              node={child}
              onEdit={onEdit}
              onDelete={onDelete}
            />
          ))}
        </ul>
      )}
    </li>
  );
}

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);

  // Form fields
  const [formData, setFormData] = useState({
    _id: null,
    productName: "",
    alias: "",
    parentCategory: "",
  });

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    setCategories(data);
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      productName: "",
      alias: "",
      parentCategory: "",
    });
    setPopupOpen(true);
  };

  const handleEdit = (category) => {
    setFormData({
      _id: category._id,
      productName: category.productName,
      alias: category.alias || "",
      parentCategory: category.parentCategory || "",
    });
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    try {
      await categoryService.deleteCategory(id);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error deleting category");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      productName: formData.productName,
      alias: formData.alias,
      parentCategory: formData.parentCategory === "" ? null : formData.parentCategory,
    };

    try {
      if (formData._id) {
        await categoryService.updateCategory(formData._id, payload);
      } else {
        await categoryService.addCategory(payload);
      }
      setPopupOpen(false);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error submitting form");
    }
  };

  // Build the nested tree
  const treeData = buildTree(categories);

  return (
    <div className="master-container">
      <h2>Category Master</h2>

      <button className="add-btn" onClick={handleAddNew}>
        + Add Category
      </button>

      {/* Tree container with scroll if too many items */}
      <div className="tree-container">
        <ul className="category-tree">
          {treeData.map((node) => (
            <CategoryNode
              key={node._id}
              node={node}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />
          ))}
        </ul>
      </div>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>
              √ó
            </button>
            <form onSubmit={handleSubmit} className="tally-form">
              <label>Name:</label>
              <input
                type="text"
                name="productName"
                value={formData.productName}
                onChange={handleChange}
                required
              />

              <label>(alias):</label>
              <input
                type="text"
                name="alias"
                value={formData.alias}
                onChange={handleChange}
              />

              <label>Under:</label>
              <select
                name="parentCategory"
                value={formData.parentCategory || ""}
                onChange={handleChange}
              >
                <option value="">Primary</option>
                {categories
                  // Don‚Äôt list itself as a parent
                  .filter((cat) => cat._id !== formData._id)
                  .map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.productName}
                    </option>
                  ))}
              </select>

              <button className="submit-btn" type="submit">
                Save
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
