import React, { useState, useEffect } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";  // Import the icons
import categoryService from "../services/categoryService";
import "../styles/MasterForms.css";

/**
 * Recursively build a nested tree from the flat list of categories.
 */
function buildTree(categories, parentId = null) {
  return categories
    .filter((cat) => String(cat.parentCategory) === String(parentId))
    .map((cat) => ({
      ...cat,
      children: buildTree(categories, cat._id),
    }));
}

const CategoryMaster = () => {
  const [categories, setCategories] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);

  // Form fields
  const [formData, setFormData] = useState({
    _id: null,
    productName: "",
    alias: "",
    parentCategory: "",
  });

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const data = await categoryService.getCategories();
    setCategories(data);
  };

  const handleAddNew = () => {
    // Reset form
    setFormData({
      _id: null,
      productName: "",
      alias: "",
      parentCategory: "",
    });
    setPopupOpen(true);
  };

  const handleEdit = (category) => {
    setFormData({
      _id: category._id,
      productName: category.productName,
      alias: category.alias || "",
      parentCategory: category.parentCategory || "",
    });
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    try {
      await categoryService.deleteCategory(id);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error deleting category");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      productName: formData.productName,
      alias: formData.alias,
      parentCategory: formData.parentCategory === "" ? null : formData.parentCategory,
    };

    try {
      if (formData._id) {
        // Update existing
        await categoryService.updateCategory(formData._id, payload);
      } else {
        // Add new
        await categoryService.addCategory(payload);
      }
      setPopupOpen(false);
      fetchCategories();
    } catch (error) {
      alert(error.response?.data?.message || "Error submitting form");
    }
  };

  // Build the nested tree once from categories
  const treeData = buildTree(categories);

  /**
   * Recursively render <tr> rows for each node in the tree.
   * @param {Array} data - Array of category nodes (with .children).
   * @param {number} level - Depth in the tree (for indentation).
   * @returns {Array<JSX.Element>} An array of <tr> elements.
   */
  const renderRows = (data, level = 0) => {
    return data.flatMap((node) => {
      // Generate indentation (4 spaces per level)
      const indentSpaces ="--".repeat(level * 4) + (level==0 ? `+` : ">");
      // Combine name + alias (if any)
      const displayName =
        indentSpaces + node.productName + (node.alias ? ` (${node.alias})` : "");

      return [
        <tr key={node._id}>
          {/* 1) Tree structure column: preserve spacing */}
          <td style={{ textAlign: "left", whiteSpace: "pre" }}>{displayName}</td>

          {/* 2) Edit button */}
          <td>
            <button className="action-btn" onClick={() => handleEdit(node)}>
            <FaEdit /> {/* Icon for edit */}
            </button>
          </td>

          {/* 3) Delete button */}
          <td>
            <button className="action-btn" onClick={() => handleDelete(node._id)}>
            <FaTrash /> {/* Icon for delete */}
            </button>
          </td>
        </tr>,

        // Recursively render the child rows
        ...renderRows(node.children, level + 1),
      ];
    });
  };

  return (
    <div className="master-container">
      <h2>Category Master</h2>

      <button className="add-btn" onClick={handleAddNew}>
        + Add Category
      </button>

      {/* Scrollable container for the table */}
      <div className="table-container">
        <table className="tree-table">
          <colgroup>
            {/* 1) 40ch for the tree structure */}
            <col style={{ width: "40ch" }} />
            {/* 2) 20ch for Edit */}
            <col style={{ width: "20ch" }} />
            {/* 3) 20ch for Delete */}
            <col style={{ width: "20ch" }} />
          </colgroup>

          {/* <thead>
            <tr>
              <th>Category Tree</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead> */}
          <tbody>{renderRows(treeData)}</tbody>
        </table>
      </div>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>
              ×
            </button>
            <form onSubmit={handleSubmit} className="tally-form">
              <label>Name:</label>
              <input
                type="text"
                name="productName"
                value={formData.productName}
                onChange={handleChange}
                required
              />

              <label>(alias):</label>
              <input
                type="text"
                name="alias"
                value={formData.alias}
                onChange={handleChange}
              />

              <label>Under:</label>
              <select
                name="parentCategory"
                value={formData.parentCategory || ""}
                onChange={handleChange}
              >
                <option value="">Primary</option>
                {categories
                  .filter((cat) => cat._id !== formData._id)
                  .map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.productName}
                    </option>
                  ))}
              </select>

              <button className="submit-btn" type="submit">
                Save
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryMaster;
