import React, { useState } from 'react';
import { addCenter } from '../services/centerService';

function CenterForm() {
    const [formData, setFormData] = useState({
        code: '',
        name: '',
        address: '',
        phoneNumber: ''
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = await addCenter(formData);
            setMessage('Centre added successfully!');
            setFormData({ code: '', name: '', address: '', phoneNumber: '' });
        } catch (err) {
            setMessage(err);
        }
    };

    return (
        <div>
            <h2>Add New Vayavach Centre</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Code</label>
                    <input
                        type="text"
                        name="code"
                        value={formData.code}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Name</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Address</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Phone Number</label>
                    <input
                        type="text"
                        name="phoneNumber"
                        value={formData.phoneNumber}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Add Centre</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default CenterForm;
