import React, { useState, useEffect } from "react";
import unitService from "../services/unitService";
import "../styles/CustomMaster.css"; // <-- Our new CSS file

const UnitMaster = () => {
  const [units, setUnits] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);

  // Form data
  const [formData, setFormData] = useState({
    _id: null,
    type: "Simple", // default
    name: "",
    symbol: "",
    decimalPlaces: 0,
  });

  // Fetch units on mount
  useEffect(() => {
    fetchUnits();
  }, []);

  const fetchUnits = async () => {
    try {
      const data = await unitService.getUnits();
      setUnits(data);
    } catch (error) {
      console.error("Error fetching units:", error);
    }
  };

  const handleAddNew = () => {
    setFormData({
      _id: null,
      type: "Simple",  // always default to "Simple"
      name: "",
      symbol: "",
      decimalPlaces: 0,
    });
    setModalOpen(true);
  };

  const handleEdit = (unit) => {
    setFormData({
      _id: unit._id,
      type: unit.type || "Simple",
      name: unit.name || "",
      symbol: unit.symbol || "",
      decimalPlaces: unit.decimalPlaces || 0,
    });
    setModalOpen(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this unit?")) return;
    try {
      await unitService.deleteUnit(id);
      fetchUnits();
    } catch (error) {
      console.error("Error deleting unit:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        type: formData.type,
        name: formData.name,
        symbol: formData.symbol,
        decimalPlaces: parseInt(formData.decimalPlaces, 10) || 0,
      };

      if (formData._id) {
        // update
        await unitService.updateUnit(formData._id, payload);
      } else {
        // add
        await unitService.addUnit(payload);
      }
      fetchUnits();
      setModalOpen(false);
    } catch (error) {
      console.error("Error saving unit:", error);
      alert("Failed to save unit.");
    }
  };

  return (
    <div className="unitMasterContainer">
      <h2 className="unitMasterHeading">Unit Master</h2>

      <button className="unitAddBtn" onClick={handleAddNew}>
        + Add Unit
      </button>

      <table className="unitTable">
        <thead>
          <tr>
            <th>Type</th>
            <th>Symbol</th>
            <th>Name</th>
            <th>Decimal Places</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {units.map((u) => (
            <tr key={u._id}>
              <td>{u.type}</td>
              <td>{u.symbol}</td>
              <td>{u.name}</td>
              <td>{u.decimalPlaces}</td>
              <td>
                <button className="unitEditBtn" onClick={() => handleEdit(u)}>
                  Edit
                </button>
                <button
                  className="unitDeleteBtn"
                  onClick={() => handleDelete(u._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {isModalOpen && (
        <div className="unitModalOverlay">
          <div className="unitModalContent">
            <button className="unitCloseBtn" onClick={() => setModalOpen(false)}>
              &times;
            </button>
            <h3>{formData._id ? "Edit Unit" : "Add Unit"}</h3>
            <form onSubmit={handleSubmit} className="unitForm">
              {/* Type */}
              <label>Type:</label>
              <input
                type="text"
                name="type"
                value={formData.type}
                onChange={handleChange}
              />

              {/* Symbol */}
              <label>Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
                required
              />

              {/* Name */}
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              {/* Decimal Places */}
              <label>Decimal Places:</label>
              <input
                type="number"
                name="decimalPlaces"
                value={formData.decimalPlaces}
                onChange={handleChange}
                required
              />

              <button type="submit" className="unitSaveBtn">
                Save
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UnitMaster;
