import React, { useState } from 'react';
import axios from 'axios';
import '../styles/MasterForm.css'; // Create this CSS file for styling if needed

function CenterForm() {
    const [formData, setFormData] = useState({
        code: '',
        name: '',
        address: '',
        phoneNumber: ''
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/centers', formData);
            setMessage('Centre added successfully!');
            setFormData({ code: '', name: '', address: '', phoneNumber: '' });
        } catch (error) {
            setMessage(error.response?.data?.message || 'Failed to add centre');
        }
    };

    return (
        <div className="center-form-container">
            <div className="center-form-card">
                <h2>Add New Vayavach Centre</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <input 
                            type="text"
                            name="code"
                            placeholder="Centre Code"
                            value={formData.code}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <input 
                            type="text"
                            name="name"
                            placeholder="Centre Name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <input 
                            type="text"
                            name="address"
                            placeholder="Address"
                            value={formData.address}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <input 
                            type="text"
                            name="phoneNumber"
                            placeholder="Phone Number"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <button type="submit" className="center-submit-button">Add Centre</button>
                </form>
                {message && <p className="center-message">{message}</p>}
            </div>
        </div>
    );
}

export default CenterForm;
