import React, { useState, useEffect } from "react";
import donationModeService from "../services/donationModeService";
import "../styles/MasterForms.css";

const DonationModeMaster = () => {
  const [modes, setModes] = useState([]);
  const [formData, setFormData] = useState({});
  const [isPopupOpen, setPopupOpen] = useState(false);

  useEffect(() => {
    fetchModes();
  }, []);

  const fetchModes = async () => {
    try {
      const response = await donationModeService.getDonationModes();
      setModes(response.data);
    } catch (error) {
      console.error("Error fetching donation modes:", error);
    }
  };

  const handleAddNew = () => {
    setFormData({});
    setPopupOpen(true);
  };

  const handleEdit = (mode) => {
    setFormData(mode);
    setPopupOpen(true);
  };

  const handleDelete = async (id) => {
    await donationModeService.deleteDonationMode(id);
    fetchModes();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData._id) {
      await donationModeService.updateDonationMode(formData._id, formData);
    } else {
      await donationModeService.addDonationMode(formData);
    }
    fetchModes();
    setPopupOpen(false);
  };

  return (
    <div className="master-container">
      <h2>Donation Mode Master</h2>
      <button className="add-btn" onClick={handleAddNew}>+ Add Donation Mode</button>

      <table className="donation-mode-table">
        <thead>
          <tr>
            <th>Mode Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {modes.length === 0 ? (
            <tr>
              <td colSpan="2">No donation modes available.</td>
            </tr>
          ) : (
            modes.map((m) => (
              <tr key={m._id}>
                <td>{m.modeName}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEdit(m)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDelete(m._id)}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-container">
            <button className="close-btn" onClick={() => setPopupOpen(false)}>❌</button>
            <form onSubmit={handleSubmit}>
              <label>Donation Mode Name:</label>
              <input type="text" name="modeName" value={formData.modeName || ""} onChange={handleChange} required />

              <button className="submit-btn" type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonationModeMaster;
