import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import donorService from "../services/donorService";
import "../styles/DonorMaster.css";

const DonorMaster = () => {
  const navigate = useNavigate();
  const [donors, setDonors] = useState([]);
  const [donor, setDonor] = useState({
    fullName: "",
    address: "",
    mobile: "",
    whatsapp: "",
    email: "",
    ref: "",
    pan: "",
    birthDate: "",
    wifeName: "",
    wifeBirthDate: "",
    anniversary: "",
    donationCentre: "",
  });

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    const data = await donorService.getDonors();
    setDonors(data);
  };

  const handleChange = (e) => {
    setDonor({ ...donor, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await donorService.addDonor(donor);
    fetchDonors();
    setDonor({
      fullName: "",
      address: "",
      mobile: "",
      whatsapp: "",
      email: "",
      ref: "",
      pan: "",
      birthDate: "",
      wifeName: "",
      wifeBirthDate: "",
      anniversary: "",
      donationCentre: "",
    });
  };

  const handleDelete = async (id) => {
    await donorService.deleteDonor(id);
    fetchDonors();
  };

  return (
    <div className="donor-container">
      <h2>Donor Master</h2>
      <button onClick={() => navigate("/")}>Back</button>
      <form onSubmit={handleSubmit}>
        {Object.keys(donor).map((key) => (
          <div key={key}>
            <label>{key.replace(/([A-Z])/g, " $1").trim()}:</label>
            <input type="text" name={key} value={donor[key]} onChange={handleChange} required />
          </div>
        ))}
        <button type="submit">Add Donor</button>
      </form>
      <h3>Donor List</h3>
      <ul>
        {donors.map((d) => (
          <li key={d._id}>
            {d.fullName} - {d.mobile} - {d.email}
            <button onClick={() => handleDelete(d._id)}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}>Back to Top</button>
    </div>
  );
};

export default DonorMaster;
